import React, {Component} from 'react';
import './Competencia.css';

class Competencia extends Component{
    constructor(props){
        super(props);
        this.state = {
            nome: props.nome,
            img: props.img,
            descricao: props.descricao,
            ampliar: false,
            ampliarTexto: "Ler Mais..."
        }
        this.getDescricao = this.getDescricao.bind(this);
        this.alterarTamanho = this.alterarTamanho.bind(this);
    }

    getDescricao(){
        return this.state.descricao;
    }

    alterarTamanho(){
        if(this.state.ampliar){
            this.setState(state =>{
                return {ampliarTexto: "Ler Mais"};
            });
            this.setState(state =>{
                return {ampliar: false};
            });
        }else{
            this.setState(state =>{
                return {ampliarTexto: "Esconder"};
            });
            this.setState(state =>{
                return {ampliar: true};
            });
        }
    }

    render(){
        return(
            <div className="competencia">
                <div className="logo">{this.state.img}</div>
                <h1 className="nome">{this.state.nome}</h1>
                <p className="descricao">
                    {this.getDescricao()} 
                    <p align="right" onClick={this.alterarTamanho()}>Ler Mais...</p>
                </p>
                
            </div>
        );
    }
}

export default Competencia;
