import React, {Component} from 'react';
import './BlocoConteudo.css';
import {Link} from 'react-router-dom';

class BlocoConteudo extends Component{
    constructor(props){
        super(props);
        this.state = {
            nome: props.nome,
            img: props.img,
            descricao: props.descricao
        }
    }

    render(){
        return(
            <section className="bloco-conteudo">
                <div className="conteudo">
                    <div className="nome">{this.state.nome}</div>
                    <p className="descricao">
                        {this.state.descricao}
                    </p>
                    <Link to={this.state.link}>Ver mais</Link>
                </div>
            </section>
        );
    }
}

export default BlocoConteudo;
