import React, {Component} from 'react'

class MousePosition extends Component {
    constructor(props) {
      super(props);
  
      this.state = { x: 0, y: 0 };
      this.updateLocation = this.updateLocation.bind(this);
    }
  
    updateLocation(x, y) {
        return "width: 100px; height: 40px; background: black; color:white; position:absolute; top:"+x+"; left:"+y+";";
    }
  
    render() {
      return (
            <div style={this.updateLocation(this.props.x, this.props.y)}>
                <h4>({ this.props.x }, { this.props.y })</h4>
            </div>
      );
    }
  }

export default MousePosition;
