import React, {Component} from 'react';
import './Competencia.css';
import Modal from '../Modal/Modal';
import Pilha, { Item } from '../../Configs/pilhaModal';

class Competencia extends Component{
    constructor(props){
        super(props);
        this.state = {
            nome: props.nome,
            img: props.img,
            descricao: props.descricao,
            ampliar: false,
            ampliarTexto: "Ver mais...",
            modal: ""
        }
        this.getDescricao = this.getDescricao.bind(this);
        this.alterarTamanho = this.alterarTamanho.bind(this);
        this.getModal = this.getModal.bind(this);
    }

    getDescricao(){
        let temp = this.state.descricao.split(" ");
        temp = temp.map((key, value) => {
            if(value<29){
                return key+" ";
            }
            return "";
        }).join(" ");
        console.log(temp.length);
        console.log(this.state.descricao.length);
        if(temp.length > this.state.descricao.length){
            return temp;
        }else{
            return temp + "...";
        }
    }

    alterarTamanho(event){
        Pilha.empilha(new Item(<div></div>, this, false));
        this.setState(state => ({
            ampliar: !state.ampliar/*,
            ampliarTexto: !state.ampliar ? "Esconder" :"Ler mais...",
            modal: !state.ampliar ? this.getModal : ""*/
        }));
    }

    getModal(){
        return undefined;
    }

    render(){
        let t = <div></div>;
        return(
            <div className="competencia">
                <div className="logo">{this.state.img}</div>
                <h1 className="nome">{this.state.nome}</h1>
                <p className="descricao">
                    {this.getDescricao()} 

                </p>
                <button onClick={this.alterarTamanho}>{this.state.ampliarTexto}</button>
                <Modal component={t} father={this} visible={this.state.ampliar}></Modal>
            </div>
        );
    }
}

export default Competencia;
