import React, {Component} from 'react'

class MousePosition extends Component {
    constructor(props) {
      super(props);
  
      this.updateLocation = this.updateLocation.bind(this);
    }
  
    updateLocation(x, y) {
        return {
            width: 100+"px",
            height: 40+"px",
            background: "black",
            color:"white",
            position:"absolute",
            top: (y+40)+"px",
            left: x+"px"
        };
    }
  
    render() {
      return (
            <div style={this.updateLocation(this.props.x, this.props.y)}>
                <h4>({ this.props.x-40 }, { this.props.y-90 })</h4>
            </div>
      );
    }
  }

export default MousePosition;
