import React, {Component} from 'react'

class MousePosition extends Component {
    constructor(props) {
      super(props);
  
      this.state = { x: 0, y: 0 };
      this.updateLocation = this.updateLocation.bind(this);
    }
  
    updateLocation(x, y) {
        return {
            width: 100+"px",
            height: 40+"px",
            background: "black",
            color:"white",
            position:"absolute",
            top: x+"px",
            left: y+"px"
        };
    }
  
    render() {
      return (
            <div style={this.updateLocation(this.props.x, this.props.y)}>
                <h4>({ this.props.x }, { this.props.y })</h4>
            </div>
      );
    }
  }

export default MousePosition;
