import React, {Component} from 'react';
import './Competencia.css';

class Competencia extends Component{
    constructor(props){
        super(props);
        this.state = {
            nome: props.nome,
            img: props.img,
            descricao: props.descricao,
            ampliar: false
        }
        getDescricao = this.getDescricao.bind(this);
    }

    getDescricao(){
        return this.state.descricao;
    }

    render(){
        return(
            <div className="competencia">
                <div className="logo">{this.state.img}</div>
                <h1 className="nome">{this.state.nome}</h1>
                <p className="descricao">
                    {this.getDescricao} 
                    <p align="right">Ler Mais...</p>
                </p>
                
            </div>
        );
    }
}

export default Competencia;
