import React, {Component} from 'react';
import './Modal.css'

class Modal extends Component{

    constructor(props){
        super(props);
        this.state = {
            component: props.component,
            father: props.father
        }
        this.setInvisible = this.setInvisible.bind(this);
    }

    setInvisible(){
        this.state.father.alterarTamanho();
    }

    render(){
        if(this.props.visible){
            return(
                <div className="modal">
                    <div className="window">
                            <svg width="30" height="30" viewBox="0 0 30 30" onClick={this.setInvisible}>
                                <path d="M5 5 L25 25 M5 25 L25 5" stroke="#AAA"></path>
                            </svg>
                        <div>
                            {this.state.component}
                        </div>
                    </div>
                </div>
            );
        }else{
            return "";
        }
    }
}

export default Modal;
