import React, {Component} from 'react';
import BlocoConteudo from './BlocoConteudo';
import './ListaBlocoConteudo.css'

class ListaBlocoConteudo extends Component{
    constructor(props){
        super(props);
        this.state = {
            competencias: props.items
        }
    }

    render(){
        return(
            <div className="list-competencias">
                {this.state.competencias.map(
                        (value, i) => {
                            return (
                                <BlocoConteudo 
                                    key={i} 
                                    nome={value.nomeCompetencia} 
                                    img={value.imgCompetencia} 
                                    descricao={value.descricaoCompetencia} 
                                    link={value.link}
                                />
                            );
                        }
                    )
                }
            </div>
        );
    }
}

export default ListaBlocoConteudo;
