import React, {Component} from 'react';
import './BlocoConteudo.css';
import {Link} from 'react-router-dom';

class BlocoConteudo extends Component{
    render(){
        return(
            <section className="bloco-conteudo">
                <div className="conteudo">
                    <div className="nome">{this.props.nome}</div>
                    <p className="descricao">
                        {this.props.descricao}
                    </p>
                    <Link className="ver-mais" to={this.props.link}>Ver mais</Link>
                </div>
            </section>
        );
    }
}

export default BlocoConteudo;
